import React, { useState, useEffect, useCallback } from 'react';
import './App.css';
import SearchChat from './components/SearchChat';
import NaverMap from './components/NaverMap';

// 백엔드 서버 URL 설정
const BACKEND_URL = 'http://localhost:5000';

function App() {
  const [searchResult, setSearchResult] = useState(null);
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [backendStatus, setBackendStatus] = useState('unknown');
  const [userLocation, setUserLocation] = useState(null);

  // 백엔드 서버 상태 확인
  const checkBackendStatus = useCallback(async () => {
    try {
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 5000); // 5초 타임아웃

      const response = await fetch('http://localhost:5000/api/status', {
        signal: controller.signal,
        headers: {
          'Content-Type': 'application/json',
        },
      });

      clearTimeout(timeoutId);

      if (response.ok) {
        setBackendStatus('connected');
        console.log('백엔드 서버 연결 성공');
      } else {
        setBackendStatus('error');
        console.error('백엔드 서버 응답 오류');
      }
    } catch (error) {
      if (error.name === 'AbortError') {
        console.error('백엔드 서버 연결 시간 초과');
        setBackendStatus('timeout');
      } else {
        console.error('백엔드 서버 연결 실패:', error);
        setBackendStatus('error');
      }
    }
  }, []);

  // 백엔드 서버 상태 주기적 확인
  useEffect(() => {
    checkBackendStatus();
    const interval = setInterval(checkBackendStatus, 30000); // 30초마다 확인
    return () => clearInterval(interval);
  }, [checkBackendStatus]);

  // 백엔드 서버 상태에 따른 UI 처리
  useEffect(() => {
    if (backendStatus === 'timeout') {
      setMessages(prev => [...prev, {
        type: 'system',
        content: '서버 연결이 지연되고 있습니다. 잠시 후 다시 시도해주세요.'
      }]);
    } else if (backendStatus === 'error') {
      setMessages(prev => [...prev, {
        type: 'system',
        content: '서버 연결에 문제가 있습니다. 잠시 후 다시 시도해주세요.'
      }]);
    }
  }, [backendStatus]);

  // 사용자 위치 가져오기
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
          console.log('사용자 위치 정보 획득:', position.coords.latitude, position.coords.longitude);
        },
        (error) => {
          console.error('위치 정보 획득 실패:', error);
        }
      );
    } else {
      console.warn('이 브라우저는 위치 정보를 지원하지 않습니다.');
    }
  }, []);

  // 위치 기반 검색 함수
  const handleSearch = async (query) => {
    try {
      setMessages(prev => [...prev, { type: 'user', text: query }]);
      setIsLoading(true);

      setMessages(prev => [...prev, { 
        type: 'system', 
        text: `'${query}'를 검색 중입니다...` 
      }]);

      // 길찾기 요청인지 확인
      const isDirectionsQuery = query.includes('가는 길') || 
                              query.includes('어떻게 가') || 
                              query.includes('길찾기') ||
                              query.includes('가는 방법');

      // 검색 API 엔드포인트 결정
      let endpoint;
      if (isDirectionsQuery) {
        endpoint = '/api/search';  // 길찾기는 네이버 API 사용
      } else {
        endpoint = query.includes('맛집') || query.includes('음식점') || query.includes('식당') || 
                  query.includes('카페') || query.includes('병원') || query.includes('약국') ? 
                  '/search' : '/api/search';
      }

      // API 요청 데이터 준비
      const requestData = {
        query,
        location: userLocation
      };

      // 길찾기 요청인 경우 출발지 정보 추가
      if (isDirectionsQuery && userLocation) {
        requestData.start_location = {
          x: userLocation.lng,
          y: userLocation.lat,
          name: '현재 위치'
        };
      }

      // API 호출
      const response = await fetch(`${BACKEND_URL}${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestData)
      });
      
      if (!response.ok) {
        throw new Error(`서버 응답 오류: ${response.status} ${response.statusText}`);
      }
      
      const data = await response.json();
      
      // "검색 중" 메시지 제거
      setMessages(prev => 
        prev.filter(msg => msg.text !== `'${query}'를 검색 중입니다...`)
      );
      
      // 길찾기 결과 처리
      if (isDirectionsQuery) {
        if (data.error) {
          setMessages(prev => [...prev, { 
            type: 'system', 
            text: data.error
          }]);
        } else if (data.directions) {
          // 길찾기 결과 표시
          const route = data.directions.route?.traoptimal[0];
          if (route) {
            const summary = route.summary;
            const duration = Math.round(summary.duration / 60000); // 밀리초를 분으로 변환
            const distance = (summary.distance / 1000).toFixed(1); // 미터를 킬로미터로 변환
            
            setMessages(prev => [...prev, { 
              type: 'system', 
              text: `예상 소요 시간: ${duration}분\n이동 거리: ${distance}km`
            }]);

            // 경로 정보를 지도에 표시
            setSearchResult({
              route: route,
              start: data.start_location,
              end: data.end_location,
              userLocation: userLocation
            });
          }
        }
      }
      // RAG 검색 결과 처리
      else if (endpoint === '/search') {
        if (data.error) {
          setMessages(prev => [...prev, { 
            type: 'system', 
            text: data.error
          }]);
        } else {
          // RAG 응답 표시
          if (data.response) {
            setMessages(prev => [...prev, { 
              type: 'system', 
              text: data.response
            }]);
          }
          
          // 장소 정보가 있으면 지도에 표시
          if (data.places && data.places.length > 0) {
            const places = data.places.map(place => ({
              x: parseFloat(place.mapx),
              y: parseFloat(place.mapy),
              name: place.name,
              address: place.address,
              category: place.category,
              distance: place.distance
            }));
            
            setSearchResult({
              places,
              userLocation: userLocation
            });
          }
        }
      } else {
        // 기존 네이버 지도 API 검색 결과 처리
        processSearchResults(data, query);
      }
    } catch (error) {
      handleSearchError(error, query);
    } finally {
      setIsLoading(false);
    }
  };

  // 검색 결과 처리 함수
  const processSearchResults = (data, query) => {
    if (data.addresses && data.addresses.length > 0) {
      const result = data.addresses[0];
      
      setSearchResult({
        x: parseFloat(result.x),
        y: parseFloat(result.y),
        address: result.roadAddress || result.jibunAddress,
        query: query,
        additionalInfo: data.additional_info || null
      });
      
      // 기본 응답 메시지
      let responseText = `'${query}'에 대한 검색 결과: ${result.roadAddress || result.jibunAddress}`;
      
      setMessages(prev => [...prev, { 
        type: 'system', 
        text: responseText 
      }]);
      
      // 추가 정보가 있으면 별도 메시지로 표시
      if (data.additional_info) {
        setMessages(prev => [...prev, { 
          type: 'system', 
          text: data.additional_info,
          isContextInfo: true
        }]);
      }
    } else if (data.error) {
      setMessages(prev => [...prev, { 
        type: 'system', 
        text: data.error
      }]);
    } else {
      setMessages(prev => [...prev, { 
        type: 'system', 
        text: `'${query}'에 대한 검색 결과가 없습니다.` 
      }]);
    }
  };

  // 검색 오류 처리 함수
  const handleSearchError = (error, query) => {
    console.error('검색 오류:', error);
    
    setMessages(prev => 
      prev.filter(msg => 
        msg.text !== `'${query}'를 검색 중입니다...` && 
        msg.text !== `'${query}'에 대해 검색 중입니다...`
      )
    );
    
    setMessages(prev => [...prev, { 
      type: 'system', 
      text: `검색 중 오류가 발생했습니다: ${error.message}` 
    }]);
  };

  // 백엔드 상태에 따른 메시지 렌더링
  const getBackendStatusMessage = () => {
    switch(backendStatus) {
      case 'offline':
        return '백엔드 서버에 연결할 수 없습니다. 서버가 실행 중인지 확인하세요.';
      case 'timeout':
        return '백엔드 서버 응답 시간이 초과되었습니다. 서버가 과부하 상태일 수 있습니다.';
      case 'error':
        return '백엔드 서버 연결 오류가 발생했습니다. 서버 로그를 확인하세요.';
      default:
        return '백엔드 서버 연결 상태를 확인할 수 없습니다.';
    }
  };

  return (
    <div className="app">
      {(backendStatus !== 'online' && backendStatus !== 'unknown') && (
        <div className="backend-status-warning">
          <p>{getBackendStatusMessage()}</p>
        </div>
      )}
      {userLocation && (
        <div className="location-status">
          <p>현재 위치 정보 사용 중</p>
        </div>
      )}
      <div className="app-container">
        <div className="map-container">
          <NaverMap 
            searchResult={searchResult} 
            userLocation={userLocation}
          />
        </div>
        <div className="chat-container">
          <SearchChat 
            onSearch={handleSearch}
            messages={messages} 
            isLoading={isLoading}
            isBackendOnline={backendStatus === 'online'}
            hasLocation={!!userLocation}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
