import React, { useEffect, useRef, useState } from 'react';
import './NaverMap.css';
import './DirectionsSearch.css';
import './DirectionsResult.css';

const NaverMap = ({ searchResult, userLocation }) => {
  const mapRef = useRef(null);
  const [isMapLoaded, setIsMapLoaded] = useState(false);

  // ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ API Î°úÎìú ÌôïÏù∏
  useEffect(() => {
    const checkMapLoad = () => {
      if (window.naver && window.naver.maps) {
        setIsMapLoaded(true);
      } else {
        setTimeout(checkMapLoad, 100);
      }
    };
    checkMapLoad();
  }, []);

  // ÏßÄÎèÑ Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    if (!isMapLoaded || !mapRef.current || !window.naver || !window.naver.maps) return;

    try {
      console.log('‚úÖ ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ API Î°úÎìú ÏÑ±Í≥µ');

      // ‚úÖ ÏßÄÎèÑ ÏÉùÏÑ±
      const mapOptions = {
        center: new window.naver.maps.LatLng(37.5665, 126.9780),
        zoom: 14,
        mapTypeControl: false,
        mapTypeId: window.naver.maps.MapTypeId.NORMAL
      };

      const map = new window.naver.maps.Map(mapRef.current, mapOptions);

      // ‚úÖ ÏÇ¨Ïö©Ïûê ÏúÑÏπò ÎßàÏª§ ÌëúÏãú
      if (userLocation) {
        new window.naver.maps.Marker({
          position: new window.naver.maps.LatLng(userLocation.lat, userLocation.lng),
          map: map,
          icon: {
            content: '<div class="user-location-marker"></div>',
            anchor: new window.naver.maps.Point(15, 15)
          },
          title: 'ÎÇ¥ ÏúÑÏπò'
        });

        // ÏÇ¨Ïö©Ïûê ÏúÑÏπòÎ°ú ÏßÄÎèÑ Ï§ëÏã¨ Ïù¥Îèô (Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏùÑ ÎïåÎßå)
        if (!searchResult) {
          map.setCenter(new window.naver.maps.LatLng(userLocation.lat, userLocation.lng));
          map.setZoom(16);
        }
      }

      // ‚úÖ Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏûàÏùÑ Îïå ÏßÄÎèÑ ÏóÖÎç∞Ïù¥Ìä∏
      if (searchResult) {
        // Í∏∏Ï∞æÍ∏∞ Í≤∞Í≥º Ï≤òÎ¶¨
        if (searchResult.type === 'directions' && searchResult.path) {
          // Í≤ΩÎ°ú Ï¢åÌëú Î≥ÄÌôò
          const path = searchResult.path.map(([lng, lat]) => 
            new window.naver.maps.LatLng(lat, lng)
          );

          // Ï∂úÎ∞úÏßÄ ÎßàÏª§
          new window.naver.maps.Marker({
            position: path[0],
            map,
            icon: {
              content: `<div class="marker start">
                <span>${searchResult.summary?.start_name || 'Ï∂úÎ∞ú'}</span>
              </div>`,
              anchor: new window.naver.maps.Point(15, 31)
            }
          });

          // ÎèÑÏ∞©ÏßÄ ÎßàÏª§
          new window.naver.maps.Marker({
            position: path[path.length - 1],
            map,
            icon: {
              content: `<div class="marker end">
                <span>${searchResult.summary?.end_name || 'ÎèÑÏ∞©'}</span>
              </div>`,
              anchor: new window.naver.maps.Point(15, 31)
            }
          });

          // Í≤ΩÎ°úÏÑ† Í∑∏Î¶¨Í∏∞
          new window.naver.maps.Polyline({
            path: path,
            strokeColor: '#FF0000',
            strokeWeight: 8,
            strokeOpacity: 0.8,
            strokeLineCap: 'round',
            strokeLineJoin: 'round',
            map: map
          });

          // Í≤ΩÎ°úÍ∞Ä Î™®Îëê Î≥¥Ïù¥ÎèÑÎ°ù ÏßÄÎèÑ ÏòÅÏó≠ Ï°∞Ï†ï
          const bounds = new window.naver.maps.LatLngBounds();
          path.forEach(coord => bounds.extend(coord));
          
          // Í≤ΩÎ°ú Ï£ºÎ≥ÄÏóê Ï∂©Î∂ÑÌïú Ïó¨Ïú† Í≥µÍ∞ÑÏùÑ ÎëêÍ∏∞ ÏúÑÌï¥ Ìå®Îî© Í∞í Ï¶ùÍ∞Ä
          const padding = { 
            top: 100, 
            right: 100, 
            bottom: 100, 
            left: 100 
          };
          map.fitBounds(bounds, padding);
          
          // Í≤ΩÎ°úÍ∞Ä Îçî Ïûò Î≥¥Ïù¥ÎèÑÎ°ù Ï§å Î†àÎ≤® Ï¶âÏãú Ï°∞Ï†ï
          const distance = searchResult.summary?.distance || 0;
          let targetZoom;
          
          // Í±∞Î¶¨Ïóê Îî∞Î•∏ Ï†ÅÏ†àÌïú Ï§å Î†àÎ≤® ÏÑ§Ï†ï
          if (distance < 500) { // 500m ÎØ∏Îßå
            targetZoom = 16;
          } else if (distance < 1000) { // 1km ÎØ∏Îßå
            targetZoom = 15;
          } else if (distance < 2000) { // 2km ÎØ∏Îßå
            targetZoom = 14;
          } else if (distance < 5000) { // 5km ÎØ∏Îßå
            targetZoom = 13;
          } else { // 5km Ïù¥ÏÉÅ
            targetZoom = 12;
          }
          
          // ÏïΩÍ∞ÑÏùò ÏßÄÏó∞ ÌõÑ Ï§å Î†àÎ≤® Ï†ÅÏö© (fitBounds ÏôÑÎ£å ÌõÑ)
          setTimeout(() => {
            const currentZoom = map.getZoom();
            if (currentZoom > targetZoom) {
              map.setZoom(targetZoom);
            }
          }, 200);
        }
        // ÏùºÎ∞ò Ïû•ÏÜå Í≤ÄÏÉâ Í≤∞Í≥º Ï≤òÎ¶¨
        else if (searchResult.type === 'places' && searchResult.places) {
          const bounds = new window.naver.maps.LatLngBounds();
          
          searchResult.places.forEach(place => {
            const position = new window.naver.maps.LatLng(place.y, place.x);
            bounds.extend(position);
            
            new window.naver.maps.Marker({
              position: position,
              map: map,
              icon: {
                content: `<div class="marker place">${place.name}</div>`,
                anchor: new window.naver.maps.Point(15, 31)
              }
            });
          });
          
          map.fitBounds(bounds);
        }
      }
    } catch (error) {
      console.error('ÏßÄÎèÑ Ï¥àÍ∏∞Ìôî Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
    }
  }, [isMapLoaded, searchResult, userLocation]);

  return (
    <div className="map-container" style={{ height: '100vh' }}>
      <div id="map" ref={mapRef} className="naver-map">
        {!isMapLoaded ? (
          <div className="map-overlay">
            <div className="map-instructions">
              <h2>ÏßÄÎèÑ Î°úÎî© Ï§ë...</h2>
              <p>Ïû†ÏãúÎßå Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî.</p>
            </div>
          </div>
        ) : !searchResult && !userLocation && (
          <div className="map-overlay">
            <div className="map-instructions">
              <h2>ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ Í≤ÄÏÉâ</h2>
              <p>Ïò§Î•∏Ï™Ω Ï±ÑÌåÖÏ∞ΩÏóê Ïû•ÏÜå Ïù¥Î¶ÑÏù¥ÎÇò Ï£ºÏÜåÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî</p>
              <p>ÏúÑÏπò Í∏∞Î∞ò Í≤ÄÏÉâÏùÑ ÏúÑÌï¥ ÏúÑÏπò Ï†ïÎ≥¥ Í≥µÏú†Î•º ÌóàÏö©Ìï¥Ï£ºÏÑ∏Ïöî</p>
            </div>
          </div>
        )}
      </div>
      
      {/* ‚úÖ Í∏∏Ï∞æÍ∏∞ Í≤∞Í≥ºÏùº Í≤ΩÏö∞ Í±∞Î¶¨ Î∞è ÏãúÍ∞Ñ Ï†ïÎ≥¥ Ï∂úÎ†• */}
      {searchResult?.type === 'directions' && searchResult.summary && (
        <div className="directions-info">
          <p>
            üöó Í±∞Î¶¨: {(searchResult.summary.distance / 1000).toFixed(2)} km
            | üïí ÏÜåÏöî ÏãúÍ∞Ñ: {Math.round(searchResult.summary.duration / 60000)} Î∂Ñ
          </p>
        </div>
      )}
    </div>
  );
};

export default NaverMap;
