import React, { useState, useRef, useEffect } from 'react';
import './SearchChat.css';

const SearchChat = ({ 
  onSearch, 
  messages, 
  isLoading = false, 
  isBackendOnline = true,
  hasLocation = false
}) => {
  const [inputValue, setInputValue] = useState('');
  const messagesEndRef = useRef(null);

  // 새 메시지가 추가될 때마다 스크롤을 아래로 이동
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (inputValue.trim() && !isLoading) {
      onSearch(inputValue.trim());
      setInputValue('');
    }
  };

  // 위치 기반 검색 추천어
  const locationSuggestions = [
    "내 주변 맛집 추천해줘",
    "가까운 병원 어디 있어?",
    "근처 카페 알려줘",
    "내 위치에서 강남역까지 가는 길"
  ];

  // 일반 검색 추천어
  const generalSuggestions = [
    "서울 강남역",
    "부산 해운대 맛집",
    "서울역에서 명동까지 가는 길",
    "강남역에서 종로3가역까지 어떻게 가나요"
  ];

  // 추천 검색어 선택
  const handleSuggestionClick = (suggestion) => {
    if (!isLoading && isBackendOnline) {
      onSearch(suggestion);
    }
  };

  return (
    <div className="search-chat">
      <div className="chat-header">
        <h2>장소 검색</h2>
        <div className="status-indicators">
          {isBackendOnline ? (
            <span className="search-mode">자연어 검색 가능</span>
          ) : (
            <div className="backend-status">오프라인</div>
          )}
          {hasLocation && (
            <span className="location-enabled">위치 사용 중</span>
          )}
        </div>
      </div>
      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="empty-state">
            <p>여기에 장소 이름, 주소 또는 자연어로 질문하세요.</p>
            <p>예: "서울 강남역", "신사동 가로수길", "서울역에서 명동까지 가는 길" 등</p>
            
            <div className="suggestions-container">
              <p className="suggestions-title">
                {hasLocation ? "위치 기반 검색 예시:" : "검색 예시:"}
              </p>
              <div className="suggestions-list">
                {(hasLocation ? locationSuggestions : generalSuggestions).map((suggestion, index) => (
                  <button 
                    key={index} 
                    className="suggestion-button"
                    onClick={() => handleSuggestionClick(suggestion)}
                    disabled={!isBackendOnline || isLoading}
                  >
                    {suggestion}
                  </button>
                ))}
              </div>
            </div>
            
            {!isBackendOnline && (
              <p className="backend-warning">
                ⚠️ 백엔드 서버에 연결할 수 없습니다. 검색 기능이 작동하지 않을 수 있습니다.
              </p>
            )}
          </div>
        ) : (
          messages.map((msg, index) => (
            <div key={index} className={`message ${msg.type} ${msg.isContextInfo ? 'context-info' : ''}`}>
              <div className="message-content">
                {msg.type === 'user' ? (
                  <>
                    <div className="message-sender">나</div>
                    <div className="message-text">{msg.text}</div>
                  </>
                ) : (
                  <>
                    <div className="message-sender">시스템</div>
                    <div className="message-text">{msg.text}</div>
                  </>
                )}
              </div>
            </div>
          ))
        )}
        {isLoading && (
          <div className="loading-indicator">
            <div className="loading-dot"></div>
            <div className="loading-dot"></div>
            <div className="loading-dot"></div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>
      <form onSubmit={handleSubmit} className="search-form">
        <input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder={hasLocation ? "주소 검색, 길찾기 또는 질문하기 (예: 내 위치에서 강남역까지 가는 길)" : "주소 검색, 길찾기 또는 질문하기 (예: 서울역에서 명동까지 가는 법)"}
          className="search-input"
          disabled={isLoading || !isBackendOnline}
        />
        <button 
          type="submit" 
          className={`search-button ${(isLoading || !isBackendOnline) ? 'disabled' : ''}`}
          disabled={isLoading || !isBackendOnline}
        >
          {isLoading ? '검색 중...' : '검색'}
        </button>
      </form>
    </div>
  );
};

export default SearchChat;
