import React, { useEffect, useRef } from 'react';
import './NaverMap.css';

const NaverMap = ({ searchResult }) => {
  const mapRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const markerRef = useRef(null);

  // 초기 맵 로드
  useEffect(() => {
    const script = document.createElement('script');
    script.src = `https://openapi.map.naver.com/openapi/v3/maps.js?ncpClientId=07gq168p3y`;
    script.async = true;
    script.onload = () => initializeMap();
    document.head.appendChild(script);

    return () => {
      if (script.parentNode) {
        script.parentNode.removeChild(script);
      }
    };
  }, []);

  // 검색 결과가 변경될 때 지도 업데이트
  useEffect(() => {
    if (mapInstanceRef.current && searchResult) {
      updateMapWithSearchResult();
    }
  }, [searchResult]);

  const initializeMap = () => {
    if (window.naver && window.naver.maps) {
      const mapOptions = {
        center: new window.naver.maps.LatLng(37.3595704, 127.105399),
        zoom: 15,
        zoomControl: true,
        zoomControlOptions: {
          position: window.naver.maps.Position.TOP_RIGHT
        }
      };

      mapInstanceRef.current = new window.naver.maps.Map(mapRef.current, mapOptions);
    }
  };

  const updateMapWithSearchResult = () => {
    if (!searchResult || !mapInstanceRef.current) return;

    const { x, y, address, query } = searchResult;
    const position = new window.naver.maps.LatLng(y, x);
    
    // 지도 중심 이동
    mapInstanceRef.current.setCenter(position);
    mapInstanceRef.current.setZoom(16);

    // 이전 마커 제거
    if (markerRef.current) {
      markerRef.current.setMap(null);
    }

    // 새 마커 생성
    markerRef.current = new window.naver.maps.Marker({
      position,
      map: mapInstanceRef.current,
      title: query
    });

    // 정보 창 생성
    const infoWindow = new window.naver.maps.InfoWindow({
      content: `
        <div class="info-window">
          <h3>${query}</h3>
          <p>${address}</p>
        </div>
      `,
      maxWidth: 300,
      backgroundColor: "#fff",
      borderColor: "#5347AA",
      borderWidth: 2,
      anchorSize: new window.naver.maps.Size(10, 10),
      anchorColor: "#fff",
      pixelOffset: new window.naver.maps.Point(10, -10)
    });

    // 마커 클릭 시 정보 창 표시
    window.naver.maps.Event.addListener(markerRef.current, 'click', () => {
      if (infoWindow.getMap()) {
        infoWindow.close();
      } else {
        infoWindow.open(mapInstanceRef.current, markerRef.current);
      }
    });

    // 정보 창 자동 표시
    infoWindow.open(mapInstanceRef.current, markerRef.current);
  };

  return (
    <div ref={mapRef} className="naver-map" id="map">
      {!searchResult && (
        <div className="map-overlay">
          <div className="map-instructions">
            <h2>네이버 지도 검색</h2>
            <p>오른쪽 채팅창에 장소 이름을 입력하세요</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default NaverMap;
