import React, { useState, useRef, useEffect } from 'react';
import './SearchChat.css';

const Message = ({ message }) => {
  const messageClass = message.type === 'user' ? 'user-message' : 'system-message';
  
  // 메시지 내용을 줄바꿈 단위로 분리하고 가게/병원 목록만 불렛으로 처리
  const formatContent = (text) => {
    if (!text) return '';
    
    // 줄바꿈으로 분리
    const lines = text.split('\n');
    
    // 메시지 내용을 분석하여 가게/병원 목록 섹션을 식별
    let isListSection = false;
    
    return (
      <div>
        {lines.map((line, index) => {
          // 줄이 비어있으면 그대로 반환
          if (line.trim() === '') {
            return <div key={index}>&nbsp;</div>;
          }
          
          // 이름, 주소, 정보 등을 포함하는 줄은 목록의 시작일 가능성이 높음
          const isNameLine = 
            /^.{1,30}(병원|의원|의료원|보건소|식당|카페|레스토랑)/.test(line) && 
            !line.includes('추천') && 
            !line.includes('근처') && 
            !line.includes('가까운');
          
          // 주소나 전화번호 패턴이 있는지 확인
          const hasAddressOrPhone = 
            /\d{2,3}-\d{3,4}-\d{4}/.test(line) || // 전화번호 패턴
            /\d+번지/.test(line) ||              // 번지 주소 패턴
            /\d{1,3}길/.test(line);               // 길 주소 패턴
            
          // 목록 항목으로 판단되는 경우
          if (isNameLine || hasAddressOrPhone) {
            return (
              <div key={index} className="place-item">
                {line}
              </div>
            );
          }
          
          // 일반 텍스트
          return <div key={index}>{line}</div>;
        })}
      </div>
    );
  };

  return (
    <div className={`message ${messageClass}`}>
      <div className="message-content">
        {formatContent(message.text)}
      </div>
    </div>
  );
};

const SearchChat = ({ 
  onSearch, 
  messages, 
  isLoading = false, 
  isBackendOnline = true,
  hasLocation = false
}) => {
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  // 새 메시지가 추가될 때마다 스크롤을 아래로 이동
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim() && isBackendOnline) {
      onSearch(input.trim());
      setInput('');
    }
  };

  // 위치 기반 검색 추천어
  const locationSuggestions = [
    "내 주변 맛집 추천해줘",
    "가까운 병원 어디 있어?",
    "근처 카페 알려줘",
    "내 위치에서 강남역까지 가는 길"
  ];

  // 일반 검색 추천어
  const generalSuggestions = [
    "서울 강남역",
    "부산 해운대 맛집",
    "서울역에서 명동까지 가는 길",
    "강남역에서 종로3가역까지 어떻게 가나요"
  ];

  // 추천 검색어 선택
  const handleSuggestionClick = (suggestion) => {
    if (!isLoading && isBackendOnline) {
      onSearch(suggestion);
    }
  };

  return (
    <div className="search-chat">
      <div className="chat-header">
        <h2>장소 검색</h2>
        <div className="status-indicators">
          {isBackendOnline ? (
            <span className="search-mode">자연어 검색 가능</span>
          ) : (
            <div className="backend-status">오프라인</div>
          )}
          {hasLocation && (
            <span className="location-enabled">위치 사용 중</span>
          )}
        </div>
      </div>
      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="empty-state">
            <p>여기에 장소 이름, 주소 또는 자연어로 질문하세요.</p>
            <p>예: "서울 강남역", "신사동 가로수길", "서울역에서 명동까지 가는 길" 등</p>
            
            <div className="suggestions-container">
              <p className="suggestions-title">
                {hasLocation ? "위치 기반 검색 예시:" : "검색 예시:"}
              </p>
              <div className="suggestions-list">
                {(hasLocation ? locationSuggestions : generalSuggestions).map((suggestion, index) => (
                  <button 
                    key={index} 
                    className="suggestion-button"
                    onClick={() => handleSuggestionClick(suggestion)}
                    disabled={!isBackendOnline || isLoading}
                  >
                    {suggestion}
                  </button>
                ))}
              </div>
            </div>
            
            {!isBackendOnline && (
              <p className="backend-warning">
                ⚠️ 백엔드 서버에 연결할 수 없습니다. 검색 기능이 작동하지 않을 수 있습니다.
              </p>
            )}
          </div>
        ) : (
          messages.map((msg, index) => (
            <Message key={index} message={msg} />
          ))
        )}
        {isLoading && (
          <div className="message system-message">
            <div className="message-content">
              검색 중...
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>
      <form onSubmit={handleSubmit} className="input-form">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder={
            !isBackendOnline ? "서버에 연결할 수 없습니다..." :
            !hasLocation ? "위치 정보 권한이 필요합니다..." :
            "무엇을 도와드릴까요?"
          }
          disabled={!isBackendOnline || !hasLocation}
        />
        <button type="submit" disabled={!isBackendOnline || !hasLocation}>
          검색
        </button>
      </form>
    </div>
  );
};

export default SearchChat;
