import React, { useEffect, useRef, useState } from 'react';
import './NaverMap.css';
import './DirectionsSearch.css';
import './DirectionsResult.css';

const NaverMap = ({ searchResult, userLocation }) => {
  const mapRef = useRef(null);
  const [isMapLoaded, setIsMapLoaded] = useState(false);
  const markersRef = useRef([]);  // ÎßàÏª§Îì§ÏùÑ Ï†ÄÏû•Ìï† ref Ï∂îÍ∞Ä

  // Í∏∞Ï°¥ ÎßàÏª§Îì§ÏùÑ Ï†úÍ±∞ÌïòÎäî Ìï®Ïàò
  const clearMarkers = () => {
    markersRef.current.forEach(marker => {
      marker.setMap(null);  // ÏßÄÎèÑÏóêÏÑú ÎßàÏª§ Ï†úÍ±∞
    });
    markersRef.current = [];  // ÎßàÏª§ Î∞∞Ïó¥ Ï¥àÍ∏∞Ìôî
  };

  // ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ API Î°úÎìú ÌôïÏù∏
  useEffect(() => {
    const checkMapLoad = () => {
      if (window.naver && window.naver.maps) {
        setIsMapLoaded(true);
      } else {
        setTimeout(checkMapLoad, 100);
      }
    };
    checkMapLoad();
  }, []);

  // ÏßÄÎèÑ Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    if (!isMapLoaded || !mapRef.current || !window.naver || !window.naver.maps) return;

    try {
      console.log('‚úÖ ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ API Î°úÎìú ÏÑ±Í≥µ');

      // ‚úÖ ÏßÄÎèÑ ÏÉùÏÑ±
      const mapOptions = {
        center: new window.naver.maps.LatLng(37.4982517, 127.0310195), // F&F Ïã†ÏÇ¨Ïò• ÏúÑÏπò
        zoom: 17,
        mapTypeControl: false,
        mapTypeId: window.naver.maps.MapTypeId.NORMAL,
        scaleControl: true,
        logoControl: true,
        mapDataControl: false,
        zoomControl: true,
      };

      const map = new window.naver.maps.Map(mapRef.current, mapOptions);

      // ‚úÖ ÏÇ¨Ïö©Ïûê ÏúÑÏπò ÎßàÏª§ ÌëúÏãú (Ìï≠ÏÉÅ ÌëúÏãú)
      if (userLocation) {
        new window.naver.maps.Marker({
          position: new window.naver.maps.LatLng(userLocation.lat, userLocation.lng),
          map: map,
          icon: {
            content: `
              <div class="user-location-marker">
                <div class="pulse"></div>
                <div class="marker-dot"></div>
              </div>
            `,
            anchor: new window.naver.maps.Point(15, 15)
          },
          title: 'ÌòÑÏû¨ ÏúÑÏπò'
        });
      }

      // Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏûàÏùÑ Îïå ÏßÄÎèÑ ÏóÖÎç∞Ïù¥Ìä∏
      if (searchResult) {
        // Í∏∞Ï°¥ ÎßàÏª§Îì§ Ï†úÍ±∞
        clearMarkers();

        // Í∏∏Ï∞æÍ∏∞ Í≤∞Í≥º Ï≤òÎ¶¨
        if (searchResult.type === 'directions' && searchResult.path) {
          // Ï∂úÎ∞úÏßÄÏôÄ ÎèÑÏ∞©ÏßÄ Ï¢åÌëú ÏÑ§Ï†ï
          const startPosition = new window.naver.maps.LatLng(
            searchResult.start.y,
            searchResult.start.x
          );
          const endPosition = new window.naver.maps.LatLng(
            searchResult.end.y,
            searchResult.end.x
          );

          // Í≤ΩÎ°úÏùò boundsÎ•º Í≥ÑÏÇ∞
          const bounds = new window.naver.maps.LatLngBounds();
          bounds.extend(startPosition);  // Ï∂úÎ∞úÏßÄ
          bounds.extend(endPosition);    // ÎèÑÏ∞©ÏßÄ
          
          // Î®ºÏ†Ä zoom Î†àÎ≤®ÏùÑ 17Î°ú ÏÑ§Ï†ï
          map.setZoom(17);
          
          // Îëê ÏßÄÏ†êÏùò Ï§ëÍ∞Ñ ÏßÄÏ†êÏùÑ Í≥ÑÏÇ∞ÌïòÏó¨ centerÎ°ú ÏÑ§Ï†ï
          const center = bounds.getCenter();
          map.setCenter(center);

          // Ï∂úÎ∞úÏßÄ ÎßàÏª§ (F&F Ïã†ÏÇ¨Ïò• - Ï¥àÎ°ùÏÉâ)
          const startMarker = new window.naver.maps.Marker({
            position: startPosition,
            map,
            icon: {
              content: `
                <div style="position: relative;">
                  <div style="
                    width: 24px; 
                    height: 24px; 
                    background: #00B700; 
                    border-radius: 50%;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    color: white;
                    font-size: 12px;
                    font-weight: bold;
                    border: 2px solid white;
                    box-shadow: 0 2px 4px #00B700;
                  ">
                  </div>
                  <div style="
                    position: absolute;
                    white-space: nowrap;
                    top: -25px;
                    left: 50%;
                    transform: translateX(-50%);
                    background: white;
                    padding: 3px 8px;
                    border-radius: 12px;
                    font-size: 12px;
                    font-weight: bold;
                    color: #00B700;
                    box-shadow: 0 2px 4px #00B700;
                  ">
                    F&F Ïã†ÏÇ¨Ïò•
                  </div>
                </div>
              `,
              anchor: new window.naver.maps.Point(12, 12)
            }
          });

          // ÎèÑÏ∞©ÏßÄ ÎßàÏª§ (Îπ®Í∞ÑÏÉâ)
          const endMarker = new window.naver.maps.Marker({
            position: endPosition,
            map,
            icon: {
              content: `
                <div style="position: relative;">
                  <div style="
                    width: 24px; 
                    height: 24px; 
                    background: #FF0000; 
                    border-radius: 50%;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    color: white;
                    font-size: 12px;
                    font-weight: bold;
                    border: 2px solid white;
                    box-shadow: 0 2px 4px #FF0000;
                  ">
                  </div>
                  <div style="
                    position: absolute;
                    white-space: nowrap;
                    top: -25px;
                    left: 50%;
                    transform: translateX(-50%);
                    background: white;
                    padding: 3px 8px;
                    border-radius: 12px;
                    font-size: 12px;
                    font-weight: bold;
                    color: #FF0000;
                    box-shadow: 0 2px 4px #FF0000;
                  ">
                    ${searchResult.end.name}
                  </div>
                </div>
              `,
              anchor: new window.naver.maps.Point(12, 12)
            }
          });

          // ÎßàÏª§Îì§ÏùÑ Î∞∞Ïó¥Ïóê Ï†ÄÏû•
          markersRef.current.push(startMarker, endMarker);
        }
        // ÏùºÎ∞ò Ïû•ÏÜå Í≤ÄÏÉâ Í≤∞Í≥º Ï≤òÎ¶¨
        else if (searchResult.type === 'places' && searchResult.places) {
          const bounds = new window.naver.maps.LatLngBounds();
          
          searchResult.places.forEach(place => {
            const position = new window.naver.maps.LatLng(place.y, place.x);
            bounds.extend(position);
            
            new window.naver.maps.Marker({
              position: position,
              map: map,
              icon: {
                content: `<div class="marker place">${place.name}</div>`,
                anchor: new window.naver.maps.Point(15, 31)
              }
            });
          });
          
          map.fitBounds(bounds);
        }
      }
    } catch (error) {
      console.error('ÏßÄÎèÑ Ï¥àÍ∏∞Ìôî Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
    }
  }, [isMapLoaded, searchResult, userLocation]);

  return (
    <div className="map-container" style={{ height: '100vh' }}>
      <div id="map" ref={mapRef} className="naver-map">
        {!isMapLoaded ? (
          <div className="map-overlay">
            <div className="map-instructions">
              <h2>ÏßÄÎèÑ Î°úÎî© Ï§ë...</h2>
              <p>Ïû†ÏãúÎßå Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî.</p>
            </div>
          </div>
        ) : !searchResult && !userLocation && (
          <div className="map-overlay">
            <div className="map-instructions">
              <h2>ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ Í≤ÄÏÉâ</h2>
              <p>Ïò§Î•∏Ï™Ω Ï±ÑÌåÖÏ∞ΩÏóê Ïû•ÏÜå Ïù¥Î¶ÑÏù¥ÎÇò Ï£ºÏÜåÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî</p>
              <p>ÏúÑÏπò Í∏∞Î∞ò Í≤ÄÏÉâÏùÑ ÏúÑÌï¥ ÏúÑÏπò Ï†ïÎ≥¥ Í≥µÏú†Î•º ÌóàÏö©Ìï¥Ï£ºÏÑ∏Ïöî</p>
            </div>
          </div>
        )}
      </div>
      
      {/* ‚úÖ Í∏∏Ï∞æÍ∏∞ Í≤∞Í≥ºÏùº Í≤ΩÏö∞ Í±∞Î¶¨ Î∞è ÏãúÍ∞Ñ Ï†ïÎ≥¥ Ï∂úÎ†• */}
      {searchResult?.type === 'directions' && searchResult.summary && (
        <div className="directions-info">
          <p>
            üö∂‚Äç‚ôÇÔ∏è Í±∞Î¶¨: {searchResult.summary.distance}m
            | üïí ÏÜåÏöî ÏãúÍ∞Ñ: {searchResult.summary.duration_minutes} Î∂Ñ
          </p>
        </div>
      )}
    </div>
  );
};

export default NaverMap;
