import React, { useState, useEffect } from 'react';
import './App.css';
import SearchChat from './components/SearchChat';
import NaverMap from './components/NaverMap';

// 백엔드 서버 URL 설정
const BACKEND_URL = 'http://localhost:5000';

function App() {
  const [searchResult, setSearchResult] = useState(null);
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [backendStatus, setBackendStatus] = useState('unknown');

  // 백엔드 서버 상태 확인
  useEffect(() => {
    const checkBackendStatus = async () => {
      try {
        console.log('백엔드 서버 상태 확인 중...');
        const controller = new AbortController();
        const timeoutId = setTimeout(() => controller.abort(), 3000); // 3초 타임아웃

        const response = await fetch(`${BACKEND_URL}/health`, { 
          signal: controller.signal 
        });
        
        clearTimeout(timeoutId);
        
        if (response.ok) {
          setBackendStatus('online');
          console.log('백엔드 서버 연결 성공');
        } else {
          setBackendStatus('error');
          console.error('백엔드 서버 응답 오류:', await response.text());
        }
      } catch (error) {
        console.error('백엔드 서버 연결 실패:', error);
        if (error.name === 'AbortError') {
          console.error('백엔드 서버 연결 시간 초과');
          setBackendStatus('timeout');
        } else {
          setBackendStatus('offline');
        }
      }
    };

    checkBackendStatus();

    // 주기적으로 서버 상태 확인 (30초마다)
    const intervalId = setInterval(checkBackendStatus, 30000);
    return () => clearInterval(intervalId);
  }, []);

  // 검색 결과 처리 함수 (공통)
  const processSearchResults = (data, query) => {
    if (data.addresses && data.addresses.length > 0) {
      const result = data.addresses[0];
      
      // Place API에서는 placeName 필드가 추가될 수 있음
      const displayName = result.placeName || result.roadAddress || result.jibunAddress;
      
      setSearchResult({
        x: parseFloat(result.x),
        y: parseFloat(result.y),
        address: result.roadAddress || result.jibunAddress,
        placeName: result.placeName || null,
        query: query
      });
      
      setMessages(prev => [...prev, { 
        type: 'system', 
        text: `'${query}'에 대한 검색 결과: ${displayName}` 
      }]);
    } else {
      setMessages(prev => [...prev, { 
        type: 'system', 
        text: `'${query}'에 대한 검색 결과가 없습니다.` 
      }]);
    }
  };

  // 검색 오류 처리 함수 (공통)
  const handleSearchError = (error, query) => {
    console.error('검색 오류:', error);
    
    setMessages(prev => 
      prev.filter(msg => 
        msg.text !== `'${query}'를 검색 중입니다...` && 
        msg.text !== `'${query}'에 대해 검색 중입니다...`
      )
    );
    
    setMessages(prev => [...prev, { 
      type: 'system', 
      text: `검색 중 오류가 발생했습니다: ${error.message}` 
    }]);
  };

  // 일반 검색 함수 - 통합 검색 API 사용
  const handleSearch = async (query) => {
    try {
      setMessages(prev => [...prev, { type: 'user', text: query }]);
      setIsLoading(true);

      setMessages(prev => [...prev, { 
        type: 'system', 
        text: `'${query}'를 검색 중입니다...` 
      }]);

      // 통합 검색 API 사용
      const response = await fetch(`${BACKEND_URL}/api/combined-search?query=${encodeURIComponent(query)}`);
      
      if (!response.ok) {
        throw new Error(`서버 응답 오류: ${response.status} ${response.statusText}`);
      }
      
      const data = await response.json();
      
      // "검색 중" 메시지 제거
      setMessages(prev => 
        prev.filter(msg => msg.text !== `'${query}'를 검색 중입니다...`)
      );
      
      // 검색 결과 처리
      processSearchResults(data, query);
    } catch (error) {
      handleSearchError(error, query);
    } finally {
      setIsLoading(false);
    }
  };

  // 자연어 검색 함수
  const handleNLPSearch = async (query) => {
    try {
      setMessages(prev => [...prev, { type: 'user', text: query }]);
      setIsLoading(true);
      
      setMessages(prev => [...prev, { 
        type: 'system', 
        text: `'${query}'에 대해 검색 중입니다...` 
      }]);

      // 백엔드에 자연어 검색 요청
      const response = await fetch(`${BACKEND_URL}/api/nlp-search`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({ query })
      });
      
      if (!response.ok) {
        throw new Error(`서버 응답 오류: ${response.status} ${response.statusText}`);
      }
      
      const data = await response.json();
      
      // "검색 중" 메시지 제거
      setMessages(prev => 
        prev.filter(msg => msg.text !== `'${query}'에 대해 검색 중입니다...`)
      );
      
      // 검색 결과 처리
      processSearchResults(data, query);
    } catch (error) {
      handleSearchError(error, query);
    } finally {
      setIsLoading(false);
    }
  };

  // 백엔드 상태에 따른 메시지 렌더링
  const getBackendStatusMessage = () => {
    switch(backendStatus) {
      case 'offline':
        return '백엔드 서버에 연결할 수 없습니다. 서버가 실행 중인지 확인하세요.';
      case 'timeout':
        return '백엔드 서버 응답 시간이 초과되었습니다. 서버가 과부하 상태일 수 있습니다.';
      case 'error':
        return '백엔드 서버 연결 오류가 발생했습니다. 서버 로그를 확인하세요.';
      default:
        return '백엔드 서버 연결 상태를 확인할 수 없습니다.';
    }
  };

  return (
    <div className="app">
      {(backendStatus !== 'online' && backendStatus !== 'unknown') && (
        <div className="backend-status-warning">
          <p>{getBackendStatusMessage()}</p>
        </div>
      )}
      <div className="app-container">
        <div className="map-container">
          <NaverMap searchResult={searchResult} />
        </div>
        <div className="chat-container">
          <SearchChat 
            onSearch={handleSearch}
            onNLPSearch={handleNLPSearch}
            messages={messages} 
            isLoading={isLoading}
            isBackendOnline={backendStatus === 'online'}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
