import React, { useState, useEffect } from 'react';
import './App.css';
import SearchChat from './components/SearchChat';
import NaverMap from './components/NaverMap';

// 백엔드 서버 URL 설정
const BACKEND_URL = 'http://localhost:5000';

function App() {
  const [searchResult, setSearchResult] = useState(null);
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [backendStatus, setBackendStatus] = useState('unknown');
  const [userLocation, setUserLocation] = useState(null);

  // 백엔드 서버 상태 확인
  useEffect(() => {
    const checkBackendStatus = async () => {
      try {
        console.log('백엔드 서버 상태 확인 중...');
        const controller = new AbortController();
        const timeoutId = setTimeout(() => controller.abort(), 3000);

        const response = await fetch(`${BACKEND_URL}/health`, { 
          signal: controller.signal 
        });

        clearTimeout(timeoutId);

        if (response.ok) {
          setBackendStatus('online');
          console.log('백엔드 서버 연결 성공');
        } else {
          setBackendStatus('error');
          console.error('백엔드 서버 응답 오류:', await response.text());
        }
      } catch (error) {
        console.error('백엔드 서버 연결 실패:', error);
        if (error.name === 'AbortError') {
          console.error('백엔드 서버 연결 시간 초과');
          setBackendStatus('timeout');
        } else {
          setBackendStatus('offline');
        }
      }
    };

    checkBackendStatus();
    const intervalId = setInterval(checkBackendStatus, 30000);
    return () => clearInterval(intervalId);
  }, []);

  // 백엔드 서버 상태에 따른 UI 처리
  useEffect(() => {
    if (backendStatus === 'timeout' || backendStatus === 'error') {
      // 상태 메시지를 UI의 상태 표시줄에만 표시하고, 채팅 메시지로는 추가하지 않음
      console.error('서버 상태 오류:', backendStatus);
    }
  }, [backendStatus]);

  // 사용자 위치 가져오기
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
          console.log('사용자 위치 정보 획득:', position.coords.latitude, position.coords.longitude);
        },
        (error) => {
          console.error('위치 정보 획득 실패:', error);
        }
      );
    } else {
      console.warn('이 브라우저는 위치 정보를 지원하지 않습니다.');
    }
  }, []);

  // 위치 기반 검색 함수
  const handleSearch = async (query) => {
    try {
      setIsLoading(true);
      
      // 사용자 질문을 메시지에 추가
      setMessages(prev => [...prev, { 
        type: 'user', 
        text: query
      }]);

      const response = await fetch('http://localhost:5000/search', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query,
          location: userLocation
        }),
      });

      const data = await response.json();
      
      if (!response.ok) {
        if (data.error) {
          throw new Error(data.error + (data.details ? `\n상세 정보: ${data.details}` : ''));
        }
        throw new Error(`서버 응답 오류: ${response.status} ${response.statusText}`);
      }
      
      if (data.error === 'API_AUTH_ERROR') {
        setMessages(prev => [...prev, { 
          type: 'system', 
          text: data.response
        }]);
        return;
      }

      if (data.error) {
        throw new Error(data.error);
      }

      // 길찾기 응답 처리
      if (data.type === 'directions' && data.summary) {
        const summary = data.summary;
        setMessages(prev => [...prev, { 
          type: 'system', 
          text: `${summary.start_name}에서 ${summary.end_name}까지\n예상 소요 시간: ${Math.round(summary.duration / 60000)}분\n이동 거리: ${(summary.distance / 1000).toFixed(1)}km`
        }]);
        
        setSearchResult({
          type: 'directions',
          path: data.path,
          start: {
            lat: data.start[1],
            lng: data.start[0]
          },
          end: {
            lat: data.end[1],
            lng: data.end[0]
          },
          summary: {
            start_name: summary.start_name,
            end_name: summary.end_name,
            distance: summary.distance,
            duration: summary.duration
          }
        });
      }
      // 일반 장소 검색 응답 처리
      else {
        if (data.response) {
          setMessages(prev => [...prev, { 
            type: 'system', 
            text: data.response
          }]);
        }
        
        if (data.places && data.places.length > 0) {
          setSearchResult({
            type: 'places',
            places: data.places,
            userLocation: userLocation
          });
        }
      }
    } catch (error) {
      console.error('검색 오류:', error);
      setMessages(prev => [...prev, { 
        type: 'system', 
        text: `죄송합니다. 검색 중 오류가 발생했습니다.\n${error.message}`
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  // 백엔드 상태에 따른 메시지 렌더링
  const getBackendStatusMessage = () => {
    switch(backendStatus) {
      case 'offline':
        return '백엔드 서버에 연결할 수 없습니다. 서버가 실행 중인지 확인하세요.';
      case 'timeout':
        return '백엔드 서버 응답 시간이 초과되었습니다. 서버가 과부하 상태일 수 있습니다.';
      case 'error':
        return '백엔드 서버 연결 오류가 발생했습니다. 서버 로그를 확인하세요.';
      default:
        return '백엔드 서버 연결 상태를 확인할 수 없습니다.';
    }
  };

  return (
    <div className="app">
      {(backendStatus !== 'online' && backendStatus !== 'unknown') && (
        <div className="backend-status-warning">
          <p>{getBackendStatusMessage()}</p>
        </div>
      )}
      {userLocation && (
        <div className="location-status">
          <p>현재 위치 정보 사용 중</p>
        </div>
      )}
      <div className="app-container">
        <div className="map-container">
          <NaverMap 
            searchResult={searchResult} 
            userLocation={userLocation}
          />
        </div>
        <div className="chat-container">
          <SearchChat 
            onSearch={handleSearch}
            messages={messages} 
            isLoading={isLoading}
            isBackendOnline={backendStatus === 'online'}
            hasLocation={!!userLocation}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
