import React, { useState, useRef, useEffect } from 'react';
import './SearchChat.css';

const SearchChat = ({ onSearch, messages, isLoading = false, isBackendOnline = true }) => {
  const [inputValue, setInputValue] = useState('');
  const messagesEndRef = useRef(null);

  // 새 메시지가 추가될 때마다 스크롤을 아래로 이동
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (inputValue.trim() && !isLoading) {
      onSearch(inputValue.trim());
      setInputValue('');
    }
  };

  return (
    <div className="search-chat">
      <div className="chat-header">
        <h2>장소 검색</h2>
        {!isBackendOnline && (
          <div className="backend-status">오프라인</div>
        )}
      </div>
      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="empty-state">
            <p>여기에 장소 이름을 입력하여 지도에서 검색하세요.</p>
            <p>예: "서울 강남역", "코엑스", "잠실 롯데타워" 등</p>
            {!isBackendOnline && (
              <p className="backend-warning">
                ⚠️ 백엔드 서버에 연결할 수 없습니다. 검색 기능이 작동하지 않을 수 있습니다.
              </p>
            )}
          </div>
        ) : (
          messages.map((msg, index) => (
            <div key={index} className={`message ${msg.type}`}>
              <div className="message-content">
                {msg.type === 'user' ? (
                  <>
                    <div className="message-sender">나</div>
                    <div className="message-text">{msg.text}</div>
                  </>
                ) : (
                  <>
                    <div className="message-sender">시스템</div>
                    <div className="message-text">{msg.text}</div>
                  </>
                )}
              </div>
            </div>
          ))
        )}
        {isLoading && (
          <div className="loading-indicator">
            <div className="loading-dot"></div>
            <div className="loading-dot"></div>
            <div className="loading-dot"></div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>
      <form onSubmit={handleSubmit} className="search-form">
        <input
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="장소 이름을 입력하세요..."
          className="search-input"
          disabled={isLoading || !isBackendOnline}
        />
        <button 
          type="submit" 
          className={`search-button ${(isLoading || !isBackendOnline) ? 'disabled' : ''}`}
          disabled={isLoading || !isBackendOnline}
        >
          {isLoading ? '검색 중...' : '검색'}
        </button>
      </form>
    </div>
  );
};

export default SearchChat;
