import React, { useEffect, useRef, useState, useCallback } from 'react';
import './NaverMap.css';
import DirectionsSearch from './DirectionsSearch';
import DirectionsResult from './DirectionsResult';
import './DirectionsSearch.css';
import './DirectionsResult.css';

const NaverMap = ({ searchResult, userLocation }) => {
  const mapRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const markerRef = useRef(null);
  const userMarkerRef = useRef(null);
  const [mapLoaded, setMapLoaded] = useState(false);
  const [mapError, setMapError] = useState(null);
  
  // 길찾기 관련 상태
  const [directions, setDirections] = useState(null);
  const [directionsPolyline, setDirectionsPolyline] = useState(null);
  const [showDirectionsPanel, setShowDirectionsPanel] = useState(false);
  const [directionsMarkers, setDirectionsMarkers] = useState([]);
  const polylineRef = useRef(null);

  // 사용자 위치 마커 업데이트
  const updateUserLocationMarker = useCallback(() => {
    if (!mapInstanceRef.current || !userLocation) return;
    
    const position = new window.naver.maps.LatLng(userLocation.lat, userLocation.lng);
    
    // 이전 사용자 위치 마커 제거
    if (userMarkerRef.current) {
      userMarkerRef.current.setMap(null);
    }
    
    // 사용자 위치 마커 생성
    userMarkerRef.current = new window.naver.maps.Marker({
      position,
      map: mapInstanceRef.current,
      icon: {
        content: '<div class="user-location-marker"></div>',
        anchor: new window.naver.maps.Point(15, 15)
      },
      title: '내 위치'
    });
    
    // 검색 결과가 없을 때만 사용자 위치로 지도 이동
    if (!searchResult && !showDirectionsPanel) {
      mapInstanceRef.current.setCenter(position);
      mapInstanceRef.current.setZoom(16);
    }
  }, [userLocation, searchResult, showDirectionsPanel]);

  // 경로 지우기
  const clearDirections = useCallback(() => {
    if (directionsPolyline) {
      directionsPolyline.setMap(null);
      setDirectionsPolyline(null);
    }
    
    directionsMarkers.forEach(marker => {
      marker.setMap(null);
    });
    setDirectionsMarkers([]);

    if (polylineRef.current) {
      polylineRef.current.setMap(null);
      polylineRef.current = null;
    }
  }, [directionsPolyline, directionsMarkers]);

  // 지도 결과 업데이트 함수
  const updateMapWithSearchResult = useCallback(() => {
    if (!searchResult || !mapInstanceRef.current) return;

    // 기존 길찾기 정보 제거
    clearDirections();

    // 길찾기 결과인 경우
    if (searchResult.route) {
      const path = searchResult.route.path.map(coords => 
        new window.naver.maps.LatLng(coords[1], coords[0])
      );

      // 경로선 그리기
      polylineRef.current = new window.naver.maps.Polyline({
        path: path,
        strokeColor: '#5347AA',
        strokeWeight: 5,
        map: mapInstanceRef.current
      });

      // 출발지 마커
      if (searchResult.start) {
        const startMarker = new window.naver.maps.Marker({
          position: new window.naver.maps.LatLng(
            searchResult.start.y,
            searchResult.start.x
          ),
          map: mapInstanceRef.current,
          icon: {
            content: '<div class="start-location-marker">출발</div>',
            anchor: new window.naver.maps.Point(15, 30)
          }
        });
        setDirectionsMarkers(prev => [...prev, startMarker]);
      }

      // 도착지 마커
      if (searchResult.end) {
        const endMarker = new window.naver.maps.Marker({
          position: new window.naver.maps.LatLng(
            searchResult.end.y,
            searchResult.end.x
          ),
          map: mapInstanceRef.current,
          icon: {
            content: '<div class="end-location-marker">도착</div>',
            anchor: new window.naver.maps.Point(15, 30)
          }
        });
        setDirectionsMarkers(prev => [...prev, endMarker]);
      }

      // 경로가 모두 보이도록 지도 영역 조정
      const bounds = new window.naver.maps.LatLngBounds();
      path.forEach(coord => bounds.extend(coord));
      mapInstanceRef.current.fitBounds(bounds);
      return;
    }

    // 일반 검색 결과 처리
    const { x, y, address, query, additionalInfo } = searchResult;
    if (!x || !y) return;
    
    const position = new window.naver.maps.LatLng(y, x);
    
    // 지도 중심 이동
    mapInstanceRef.current.setCenter(position);
    mapInstanceRef.current.setZoom(16);

    // 이전 마커 제거
    if (markerRef.current) {
      markerRef.current.setMap(null);
    }

    // 새 마커 생성
    markerRef.current = new window.naver.maps.Marker({
      position,
      map: mapInstanceRef.current,
      title: query
    });

    // 정보 창 생성
    const infoWindow = new window.naver.maps.InfoWindow({
      content: `
        <div class="info-window">
          <h3>${query}</h3>
          <p>${address}</p>
          ${additionalInfo ? `
            <div class="additional-info">
              <p class="info-title">추가 정보</p>
              <p class="info-content">${additionalInfo}</p>
            </div>
          ` : ''}
        </div>
      `,
      maxWidth: 300,
      backgroundColor: "#fff",
      borderColor: "#5347AA",
      borderWidth: 2,
      anchorSize: new window.naver.maps.Size(10, 10),
      anchorColor: "#fff",
      pixelOffset: new window.naver.maps.Point(10, -10)
    });

    // 마커 클릭 시 정보 창 표시
    window.naver.maps.Event.addListener(markerRef.current, 'click', () => {
      if (infoWindow.getMap()) {
        infoWindow.close();
      } else {
        infoWindow.open(mapInstanceRef.current, markerRef.current);
      }
    });

    // 정보 창 자동 표시
    infoWindow.open(mapInstanceRef.current, markerRef.current);
  }, [searchResult, clearDirections]);

  // 초기 맵 로드
  useEffect(() => {
    const script = document.createElement('script');
    script.src = `https://openapi.map.naver.com/openapi/v3/maps.js?ncpClientId=07gq168p3y`;
    script.async = true;
    
    script.onload = () => {
      try {
        if (window.naver && window.naver.maps) {
          const mapOptions = {
            center: new window.naver.maps.LatLng(37.3595704, 127.105399),
            zoom: 15,
            zoomControl: true,
            zoomControlOptions: {
              position: window.naver.maps.Position.TOP_RIGHT
            }
          };

          mapInstanceRef.current = new window.naver.maps.Map(mapRef.current, mapOptions);
          setMapLoaded(true);
          console.log("네이버 지도가 성공적으로 로드되었습니다.");
        } else {
          console.error("네이버 지도 API가 로드되지 않았습니다.");
          setMapError("지도를 로드할 수 없습니다.");
        }
      } catch (error) {
        console.error("지도 초기화 중 오류:", error);
        setMapError("지도 초기화 중 오류가 발생했습니다.");
      }
    };
    
    script.onerror = (e) => {
      console.error("네이버 지도 스크립트 로드 실패:", e);
      setMapError("지도 스크립트를 로드할 수 없습니다. API 키를 확인하세요.");
    };
    
    document.head.appendChild(script);

    return () => {
      if (script.parentNode) {
        script.parentNode.removeChild(script);
      }
    };
  }, []);

  // 사용자 위치 변경 시 지도 업데이트
  useEffect(() => {
    if (mapLoaded && userLocation) {
      updateUserLocationMarker();
    }
  }, [mapLoaded, userLocation, updateUserLocationMarker]);

  // 검색 결과 변경 시 지도 업데이트
  useEffect(() => {
    if (mapLoaded && searchResult) {
      updateMapWithSearchResult();
    }
  }, [mapLoaded, searchResult, updateMapWithSearchResult]);

  return (
    <div className="map-container">
      <div ref={mapRef} className="naver-map" id="map">
        {!searchResult && !userLocation && !mapError && (
          <div className="map-overlay">
            <div className="map-instructions">
              <h2>네이버 지도 검색</h2>
              <p>오른쪽 채팅창에 장소 이름이나 주소를 입력하세요</p>
              <p>위치 기반 검색을 위해 위치 정보 공유를 허용해주세요</p>
            </div>
          </div>
        )}
        
        {mapError && (
          <div className="map-error-overlay">
            <div className="map-error">
              <h2>지도 로드 오류</h2>
              <p>{mapError}</p>
            </div>
          </div>
        )}
      </div>
      
      <div className="directions-container">
        <DirectionsSearch 
          onSearch={handleDirectionsSearch} 
          userLocation={userLocation}
        />
      </div>
      
      {showDirectionsPanel && directions && (
        <div className="directions-panel">
          <DirectionsResult 
            directions={directions} 
            onClose={handleCloseDirections}
          />
        </div>
      )}
    </div>
  );
};

export default NaverMap;
